import { Preconditions } from '../../../base/preconditions';
import { FOCUS_LOCK_EXCEPTED_ATTR } from '../a11y/focus_lock/dom_utils';
export const Internal = {
    copyApi: async function copyApi(str, clipboard) {
        try {
            await clipboard.writeText(str);
        } catch (e) {
            return Internal.copyLegacy(str, document);
        }
    },
    copyLegacy: async function copyLegacy(str, document1) {
        const el = document1.createElement('input');
        el.setAttribute(FOCUS_LOCK_EXCEPTED_ATTR, 'true');
        el.style.position = 'absolute';
        el.style.left = '-9999px';
        el.value = str;
        el.readOnly = true;
        try {
            document1.body.appendChild(el);
            const range = document1.createRange();
            range.selectNodeContents(el);
            const selection = document1.getSelection();
            selection.removeAllRanges();
            selection.addRange(range);
            el.select();
            el.setSelectionRange(0, str.length);
            Preconditions.checkState(document1.execCommand('copy'), 'Copy must be successful');
        } finally{
            el.remove();
        }
    }
};
export function createClipboard() {
    return (str, _window = window)=>{
        if (_window.navigator.clipboard != null) return Internal.copyApi(str, _window.navigator.clipboard);
        return Internal.copyLegacy(str, _window.document);
    };
}
