import { useExpectedPixelsPerRem } from '../provider/provider';
import styles from './metrics.css';
export const baseUnit = parseInt(styles.baseUnit, 10) || 8;
export const Breakpoint = {
  DEFAULT: 0,
  SMALL: 1,
  MEDIUM: 2,
  LARGE: 3,
  XLARGE: 4
};
export const smallBreakpoint = parseInt(styles.smallBreakpoint, 10) || 600;
export const mediumBreakpoint = parseInt(styles.mediumBreakpoint, 10) || 900;
export const largeBreakpoint = parseInt(styles.largeBreakpoint, 10) || 1200;
export const xLargeBreakpoint = parseInt(styles.xLargeBreakpoint, 10) || 1650;
export const breakpointFromScreenWidth = width => {
  if (width >= xLargeBreakpoint) return 4;else if (width >= largeBreakpoint) return 3;else if (width >= mediumBreakpoint) return 2;else if (width >= smallBreakpoint) return 1;else return 0;
};
export const aboveSmall = breakpoint => breakpoint >= 1;
export const belowSmall = breakpoint => breakpoint < 1;
export const aboveMedium = breakpoint => breakpoint >= 2;
export const belowMedium = breakpoint => breakpoint < 2;
export const aboveLarge = breakpoint => breakpoint >= 3;
export const belowLarge = breakpoint => breakpoint < 3;
export const aboveXLarge = breakpoint => breakpoint >= 4;
export const belowXLarge = breakpoint => breakpoint < 4;
export const unitSpaces = ['0', '0.25u', '0.5u', '1u', '1.5u', '2u', '3u', '4u', '6u', '8u', '12u'];
export const getSpaceValue = space => {
  return spaceValues[space];
};
const spaceValues = {
  ['0']: styles.space0,
  ['0.25u']: styles.space025,
  ['0.5u']: styles.space050,
  ['1u']: styles.space1,
  ['1.5u']: styles.space150,
  ['2u']: styles.space2,
  ['3u']: styles.space3,
  ['4u']: styles.space4,
  ['6u']: styles.space6,
  ['8u']: styles.space8,
  ['12u']: styles.space12
};
export const unitSizeToNumber = unitSize => {
  const numeric = unitSize.slice(0, -1);
  return Number(numeric) * baseUnit;
};
export const resolveUnitOrNumberSize = size => {
  if (size == null || typeof size === 'number') return size;
  return unitSizeToNumber(size);
};
export const DEFAULT_ROOT_FONT_SIZE = 10;
export const useBaseUnitInRem = () => {
  const rootFontSize = useExpectedPixelsPerRem() || DEFAULT_ROOT_FONT_SIZE;
  return baseUnit / rootFontSize;
};
export const getRootFontSize = () => {
  if (window == null) return DEFAULT_ROOT_FONT_SIZE;
  return parseFloat(getComputedStyle(window.document.documentElement).fontSize) || DEFAULT_ROOT_FONT_SIZE;
};
export const useTextZoomBaseUnit = (currentRootFontSize = getRootFontSize()) => {
  const originalRootFontSize = useExpectedPixelsPerRem() || DEFAULT_ROOT_FONT_SIZE;
  const textZoomLevel = currentRootFontSize / originalRootFontSize;
  return baseUnit * textZoomLevel;
};